import React from 'react';
import data from '../data';
import Trivia from './Trivia';

class TriviaContainer extends React.Component {
    state = {  
        score: 0,
        triviaQuestions: data.questions
    }

    getRandom(arr, n) {
        let result = new Array(n),
            len = arr.length,
            taken = new Array(len);
        if (n > len)
            throw new RangeError("getRandom: more elements taken than available");
        while (n--) {
            let x = Math.floor(Math.random() * len);
            result[n] = arr[x in taken ? taken[x] : x];
            taken[x] = --len in taken ? taken[len] : len;
        }
        return result;
    }

    handleBtnClick = () => {
        console.log("working")
    }

    renderQuestions = () => {
        return this.getRandom(this.state.triviaQuestions, 4).map(question => 
            <Trivia key={question.text} questions={question} handleBtnClick={this.handleBtnClick}/>
        )
    }
    render() { 
        return (  
            <>
            <h1>Lutrivia</h1>
            <h2>Score: </h2>
            {this.renderQuestions()}
            </>
        );
    }
}

export default TriviaContainer;
