import React from "react";

const Trivia = (props) => {
    return (
        <div className="triviaQuestion">
            <h1 id="propHeader">{props.question.text}</h1>
            <button className="trueFalseBtns" value="true" onClick={() => props.handleClick(props.question, event)}>True</button><button className="trueFalseBtns" onClick={() => props.handleClick(props.question)}>False</button>
        </div>
    )
}

export default Trivia
