import React from 'react';
import data from '../data';
import Trivia from './Trivia';

class TriviaContainer extends React.Component {
    state = {  
        score: 0,
        triviaQuestions: data.questions
    }



    handleBtnClick = () => {
        this.setState(prevState => ({
            score: prevState.score + 1,
            ...this.state.triviaQuestions
        }))
    }

    renderQuestions = () => {
        let randomArr = this.getRandom(this.state.triviaQuestions, 4)
        return randomArr.map(question => 
            <Trivia key={question.text} questions={question} handleBtnClick={this.handleBtnClick}/>
        )
    }
    render() { 
        return (  
            <>
            <h1>Lutrivia</h1>
        <h2>Score: {this.state.score}</h2>
            {this.renderQuestions()}
            </>
        );
    }
}

export default TriviaContainer;
